"use strict";angular.module("sbAdminApp").controller("TableCtrl",function($scope){function getDayClass(data){var date=data.date,mode=data.mode;if("day"===mode)for(var dayToCheck=new Date(date).setHours(0,0,0,0),i=0;i<$scope.events.length;i++){var currentDay=new Date($scope.events[i].date).setHours(0,0,0,0);if(dayToCheck===currentDay)return $scope.events[i].status}return""}$scope.today=function(){console.log("DatePickerCtrl is here"),$scope.dt=new Date},$scope.today(),$scope.clear=function(){$scope.dt=null},$scope.options={customClass:getDayClass,minDate:new Date,showWeeks:!0},$scope.toggleMin=function(){$scope.options.minDate=$scope.options.minDate?null:new Date},$scope.toggleMin(),$scope.setDate=function(year,month,day){$scope.dt=new Date(year,month,day)};var tomorrow=new Date;tomorrow.setDate(tomorrow.getDate()+1);var afterTomorrow=new Date(tomorrow);afterTomorrow.setDate(tomorrow.getDate()+1),$scope.events=[{date:tomorrow,status:"full"},{date:afterTomorrow,status:"partially"}]});